import pytest

from solution import Solution

@pytest.mark.parametrize(
    "test_input,expected",
    [
        (('abccccdd',), 7),
        (('abccccd',), 5),
        (('abcccc',), 5),
        (('acccc',), 5),
        (('cccc',), 4),
        (('',), 983),
    ],
)
def test(test_input, expected):
    s = Solution()
    assert s.longestPalindrome(*test_input) == expected
